import "./navigation.css";
function Navigation() {
    const list_menu = {
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        gap: '50px'
    };
    const listyle = {
        listStyle: "none"
    }
    const $ = document.querySelector.bind(document)
    const $$ = document.querySelectorAll.bind(document)
    const tabs = $$('.btn-a');
   // console.log(tabs);
   tabs.forEach((tab, index) => {
    tab.onclick = function() {
        $('.btn-a.change_color').classList.remove('change_color')
        this.classList.add('change_color')
    }
   })
    return (
        <ul style={list_menu}>
                <li style={listyle}>
                    <a  className="btn-a" href="#">Home</a>
                    </li>
                <li style={listyle}>
                    <a  className="btn-a" href="#">About</a>
                    </li>
                <li style={listyle}>
                    <a  className="btn-a" href="#">Tech Stack</a>
                    </li>
                <li style={listyle}>
                    <a  className="btn-a" href="#">Projects</a>
                    </li>
                <li style={listyle}>
                    <a  className="btn-a" href="#">Contact</a>
                </li>
        </ul>
    )
};
export default Navigation;

